import React, {useEffect, useState} from 'react'
import "./style.css"
import {slownikTab} from "./slownik.js"

function Gra() {
    const [haslo, setHaslo] = useState('');
    const [slowoGracza, setSlowoGracza] = useState('');

    const [tablica, setTablica] = useState([{ backgroundColor: "white" }, { backgroundColor: "white" }, { backgroundColor: "white" }, { backgroundColor: "white" }, { backgroundColor: "white" }]);

    const [litera, setLitera] = useState('');
    const [litera2, setLitera2] = useState('');
    const [litera3, setLitera3] = useState('');
    const [litera4, setLitera4] = useState('');
    const [litera5, setLitera5] = useState('');

    const [step, setStep] = useState(0);
 //   const tablica = [{ "backgroundColor": "white" }, { "backgroundColor": "white" }, { "backgroundColor": "white" }, { "backgroundColor": "white" }, { "backgroundColor": "white" }];


  //  const kolor = ({ "backgroundColor": "white" });

    const losowanie = () => {
    setHaslo(slownikTab[Math.floor(Math.random() * slownikTab.length)]);
    setStep(step + 1);
    console.log(haslo);
    }

    useEffect(() => {
        setSlowoGracza(litera + litera2 + litera3 + litera4 + litera5);
      });

    const sprawdz = () => {
        litery(slowoGracza, haslo, tablica);
    }

    const litery = (slowo, haslo, tablica) => {
        for (var i = 0; i < slowo.length; i++) {
            for (var j = 0; j < haslo.length; j++){
                if (slowo.charAt(i) === haslo.charAt(j)){
                    console.log("jest równe! pomaranczowe");
                    tablica[i] = ({ backgroundColor: "orange" });
                    setTablica([...tablica]);
                }
            }

            if (slowo.charAt(i) === haslo.charAt(i)){
                console.log("jest równe, zielone");
                tablica[i] = { backgroundColor: "green" };
                setTablica([...tablica]);
                console.log(tablica[i]);

            } 

        }
        console.log(tablica);
        return tablica;
    }





  return (
    <div className='gra-head'>

    {(() => {
        if (step===0) {
            return (
                <div className='div-losowanie'>
                    <button onClick={losowanie} className="button-losuj">Losuj</button>
                </div>
            )
        }
    })()}

    {(() => {
         if (step===1) {
            return (
                <div>
                <div className='inputs'>
                    <input
                        maxLength="1"
                        className='input'
                        type="text"
                        value= {litera}
                        onChange={e => setLitera(e.target.value)}
                        style= { tablica[0] }
                    />
                    <input
                        maxLength="1"
                        className='input'
                        type="text"
                        value= {litera2}
                        onChange={e => setLitera2(e.target.value)}
                        style= { tablica[1] }
                    />
                    <input
                        maxLength="1"
                        className='input'
                        type="text"
                        value= {litera3}
                        onChange={e => setLitera3(e.target.value)}
                        style= { tablica[2] }
                    />
                    <input
                        maxLength="1"
                        className='input'
                        type="text"
                        value= {litera4}
                        onChange={e => setLitera4(e.target.value)}
                        style= { tablica[3] }
                    />
                    <input
                        maxLength="1"
                        className='input'
                        type="text"
                        value= {litera5}
                        onChange={e => setLitera5(e.target.value)}
                        style= { tablica[4] }
                    />

                    <button onClick={sprawdz} className="button-zatwierdz">Zatwierdź</button>
                </div>
                <div className='inputs'>
                    <input
                        maxLength="1"
