import React, {useState} from 'react'
import "./style.css"
import {slownikTab} from "./slownik.js"
import RICIBs from 'react-individual-character-input-boxes';

function Gra() {
    const [haslo, setHaslo] = useState('');
    const [slowoGracza, setSlowoGracza] = useState('');
    const [odpowiedz, setOdpowiedz] = useState('');

    const [tablica, setTablica] = useState([{ "backgroundColor": "white" }, { "backgroundColor": "white" }, { "backgroundColor": "white" }, { "backgroundColor": "white" }, { "backgroundColor": "white" }]);



  //  const kolor = ({ "backgroundColor": "white" });

    const losowanie = () => {
    setHaslo(slownikTab[Math.floor(Math.random() * slownikTab.length)]);
    }

    const sprawdz = () => {
        if(slowoGracza.length < 5){
            setOdpowiedz("Hasło jest za krótkie")
        } else if(slowoGracza.length > 5){
            setOdpowiedz("Hasło jest zbyt długie")
        } else{
            setOdpowiedz("Hasło jest OK");
            litery(slowoGracza, haslo, tablica);
        } 
    }

    const litery = (slowo, haslo, tablica) => {
        for (var i = 0; i < slowo.length; i++) {
            if (slowo.charAt(i) === haslo.charAt(i)){
                console.log("jest równe, zielone");

            //    let newArr = [];
            //    newArr[i] = ({ "backgroundColor": "green" });
            //    setTablica(newArr);

            //    console.log(tablica[i]);

            } 
            for (var j = 0; j < haslo.length; j++){
                if (slowo.charAt(i) === haslo.charAt(j)){
                    console.log("jest równe! (pomaranczowe");
                }
            }
        }
    }


    const handleOutput = (string) => {
        setSlowoGracza(string)
    }

    const proba = index => {
        setTablica(existingItems => {
          return [
            ...existingItems.slice(0, index),
            existingItems[index] + 1,
            ...existingItems.slice(index + 1),
          ]
        })
      }







  return (
    <div className='gra-head'>
        <div className='gra-body'>

        </div>

    <button onClick={losowanie}>Losuj</button>

    <h1>{haslo}</h1>


    <button onClick={sprawdz}>Zatwierdź</button>

    <h1>{odpowiedz}</h1>

    <h1>{slowoGracza}</h1>


    <RICIBs
        amount={5}
        autoFocus
        handleOutputString={handleOutput}
        inputProps={[
            { style: tablica[0] },
            { style: tablica[1]},
            { style: tablica[2]},
            { style: tablica[3]},
            { style: tablica[4]}
        ]}
        inputRegExp={/^[a-z]$/}
    />

  //  <button onClick={proba(0)}>Proba</button>


    </div>
  )
}

export default Gra
